mapper.select:
SELECT * FROM (SELECT ROWNUM NO, N.* FROM (SELECT OH1.*, OH2.PA_NO, OH2.PA_ATTACH FROM ( SELECT OHA.NICKNAME, OHA.PROFILEIMG, OHB.* FROM MY_MEMBER_INFO OHA INNER JOIN OH_PHOTO_BOARD OHB ON OHA.MEMNO = OHB.MEMNO ) OH1 LEFT OUTER JOIN (SELECT * FROM OH_PHOTO_ATTACH WHERE PA_NO IN (SELECT MIN(PA_NO) FROM OH_PHOTO_ATTACH GROUP BY PB_NO ) ) OH2 ON OH1.PB_NO = OH2.PB_NO
ORDER BY PB_DATE DESC
ORDER BY PB_DATE ASC
ORDER BY PB_LIKE DESC
ORDER BY PB_LIKE ASC
ORDER BY PB_HIT DESC
ORDER BY PB_HIT ASC
) N
AND PB_RESIDENCE = #{param5}
AND PB_ROOM = #{param6}
AND PB_STYLE = #{param7}
AND PB_SKILL = #{param8}
AND (PB_TITLE LIKE '%'||#{param10}||'%' OR PB_CONTENT LIKE '%'||#{param10}||'%')
AND (PB_TITLE LIKE '%'||#{param10}||'%')
AND (PB_CONTENT LIKE '%'||#{param10}||'%')
) WHERE NO BETWEEN #{param1} AND #{param2}

SELECT 
	COUNT(*) 
FROM 
	OH_PHOTO_BOARD


		AND PB_RESIDENCE = #{param1}


		AND PB_ROOM = #{param2}


		AND PB_STYLE = #{param3}


		AND PB_SKILL = #{param4}



			AND (PB_TITLE LIKE '%'||#{param6}||'%' OR PB_CONTENT LIKE '%'||#{param6}||'%')


			AND (PB_TITLE LIKE '%'||#{param6}||'%')


			AND (PB_CONTENT LIKE '%'||#{param6}||'%')

