mapper.select:
SELECT PRODUCT_ID, SELLER_ID, CATEGORY_ID, NAME, REP_PRICE, REP_DISCOUNTED_PRICE, IS_DISCOUNTED, DISCOUNT_RATE, DELIVERY_FEE, DELIVERY_TYPE, REF_PRICE, OPTION_TYPE, OPTION1_SET_ID, OPTION2_SET_ID, REG_TIME, HITS, SALES, SCRAPS, REVIEWS FROM SHOP_PRODUCT WHERE CATEGORY_ID IN
#{item.category_id}
ORDER BY PRODUCT_ID

mapper.insert:
DECLARE BASKET_COUNT NUMBER; BEGIN
SELECT COUNT(*) INTO BASKET_COUNT FROM SHOP_BASKET WHERE USER_ID = #{item.user_id} AND OPTION_ID = #{item.option_id};
IF BASKET_COUNT != 0 THEN UPDATE SHOP_BASKET SET QUANTITY = QUANTITY + #{item.quantity} WHERE USER_ID = #{item.user_id} AND OPTION_ID = #{item.option_id}; ELSE INSERT INTO SHOP_BASKET ( BASKET_ID, USER_ID, PRODUCT_ID, OPTION_ID, QUANTITY, OPTION_TEXT) VALUES ( SEQ_SHOP_BASKET.NEXTVAL, #{item.user_id}, #{item.product_id}, #{item.option_id}, #{item.quantity}, #{item.option_text}); END IF;
END;

mapper.delete:
DELETE
	SHOP_BASKET
WHERE


		USER_ID = #{param1} AND PRODUCT_ID = #{param2}


		USER_ID = #{param1} AND OPTION_ID = #{param2}
